CREATE TABLE IF NOT EXISTS usuario (USERNAME VARCHAR(30) NOT NULL, APELLIDO VARCHAR(30), DIRECCION VARCHAR(30), NOMBRE VARCHAR(30), PASSWORD VARCHAR(30), TELEFONO VARCHAR(30), PRIMARY KEY (USERNAME));

CREATE TABLE IF NOT EXISTS proveedor (USERNAME VARCHAR(30) NOT NULL, APELLIDO VARCHAR(30), DIRECCION VARCHAR(30), NOMBRE VARCHAR(30), PASSWORD VARCHAR(30), TELEFONO VARCHAR(30), PRIMARY KEY (USERNAME));